(()=>{const deadlineDate=new Date("December 29, 2019 23:59:59").getTime();const countdownMonths=document.querySelector(".countdown__months .number");const countdownDays=document.querySelector(".countdown__days .number");const countdownHours=document.querySelector(".countdown__hours .number");const countdownMinutes=document.querySelector(".countdown__minutes .number");const countdownSeconds=document.querySelector(".countdown__seconds .number");setInterval(()=>{const currentDate=(new Date).getTime();const distance=deadlineDate-currentDate;const months=Math.floor(distance/(1e3*60*60*24*30.4375));const days=Math.floor(distance%(1e3*60*60*24*30.4375)/(1e3*60*60*24));const hours=Math.floor(distance%(1e3*60*60*24)/(1e3*60*60));const minutes=Math.floor(distance%(1e3*60*60)/(1e3*60));const seconds=Math.floor(distance%(1e3*60)/1e3);countdownMonths.innerHTML=months;countdownDays.innerHTML=days;countdownHours.innerHTML=hours;countdownMinutes.innerHTML=minutes;countdownSeconds.innerHTML=seconds},1e3)})();